<?php     if ( ! defined( 'ABSPATH' ) ) exit;    function easyform_default_values() {        return array(            // Define default values for each option here            'easyform_enable' => '',            'easyform_newsletter_status' => 'on', // Default countdown timer level color            'easyform_client_amount' => '1',            'easyform_client_buyer' => '1',            'easyform_client_receipt_id' => '1',            'easyform_client_items' => '1',            'easyform_client_email' => '1',            'easyform_client_ip' => '1',            'easyform_client_note' => '1',            'easyform_client_city' => '1',            'easyform_client_phone' => '1',            'easyform_client_hash_key' => '1',            'easyform_client_entry_by' => '1',        );}// Check and set default values for options if they don't existfunction easyform_check_default_values() {    $default_values = easyform_default_values();    foreach ($default_values as $option_name => $default_value) {        if (!get_option($option_name)) {            update_option($option_name, $default_value);        }    }}        // Call the function to check and set default values on plugin initializationeasyform_check_default_values();